mapper.select:
SELECT b.board_no AS boardNo, b.board_category_no AS boardCategoryNo, b.member_no AS memberNo, b.board_order AS boardOrder, b.board_depth AS boardDepth, b.board_title AS boardTitle, b.board_content AS boardContent, b.board_regDate AS boardRegDate, b.board_viewCount AS boardViewCount, c.board_category_name AS boardCategoryName, m.member_id AS memberId FROM t_board b INNER JOIN t_board_category c INNER JOIN t_member m ON b.board_category_no = c.board_category_no AND b.member_no = m.member_no WHERE b.board_category_no = #{boardCategoryNo}
AND b.board_title LIKE CONCAT('%',#{searchWord},'%')
ORDER BY b.board_no DESC LIMIT #{startRow}, #{rowPerPage}

mapper.update:
UPDATE t_board
SET
	board_viewCount = #{boardViewCount}
WHERE board_no = #{boardNo}
