mapper.insert:
	INSERT
	INTO t_facility(member_no
					,facility_name
					,facility_phone
					,facility_address
					,facility_notice
					,facility_detail
					,facility_price
					,facility_refund
					,facility_rule
					,facility_licensee_no
					,facility_return
					,facility_status
					,facility_write_date)
	VALUES(#{memberNo}
			,#{facilityName}
			,#{facilityPhone}
			,#{facilityAddress}
			,#{facilityNotice}
			,#{facilityDetail}
			,#{facilityPrice}
			,#{facilityRefund}
			,#{facilityRule}
			,#{facilityLicenseeNo}
			,#{facilityReturn}
			,#{facilityStatus}
			,now())

	SELECT LAST_INSERT_ID()

mapper.update:
UPDATE t_facility
	SET facility_name = #{facilityName}
		,facility_phone = #{facilityPhone}
		,facility_address = #{facilityAddress}
		,facility_notice = #{facilityNotice}
		,facility_detail = #{facilityDetail}
		,facility_price = #{facilityPrice}
		,facility_refund = #{facilityRefund}
		,facility_rule = #{facilityRule}
		,facility_licensee_no = #{facilityLicenseeNo}
	WHERE facility_no = #{facilityNo}
