mapper.delete:
DELETE 
FROM t_match_join_member
WHERE match_notice_no = #{matchNoticeNo} AND member_no = #{memberNo}

mapper.select:
SELECT n.match_notice_no AS matchNoticeNo, n.match_expected_day AS matchExpectedDay, n.match_expected_start_time AS matchExpectedStartTime, n.match_expected_end_time AS matchExpectedEndTime, n.member_no AS memberNo, n.match_notice_status AS matchNoticeStatus, n.match_kinds AS matchKinds, n.match_notice_region AS matchNoticeRegion, n.match_member_count AS matchMemberCount, n.match_notice_insert_date AS matchNoticeInsertDate, j.team_no AS teamNo, j.home_away AS homeAway FROM t_match_notice AS n INNER JOIN t_match_join_member AS j ON n.match_notice_no = j.match_notice_no WHERE sport_entries_no = #{sportEntriesNo}
AND match_expected_day > now()
AND match_expected_day >= #{ matchExpectedDay}
AND match_kinds = #{matchKinds}
AND match_notice_status = #{matchNoticeStatus}
AND home_away = #{homeAway}
AND match_member_count = #{matchMemberCount}
AND team_no = #{teamNo}
GROUP BY matchNoticeNo

SELECT 
	m.member_id	AS memberId,
	m.member_no AS memberNo	
FROM t_match_join_member j INNER JOIN t_member m
ON j.member_no = m.member_no
WHERE j.match_notice_no = #{matchNoticeNo}

		AND j.home_away = #{homeAway}


		AND j.team_no = #{teamNo}
