mapper.select:
SELECT team_no AS teamNo ,sport_entries_no AS sportEntriesNo ,team_name AS teamName FROM t_team WHERE sport_entries_no = #{sportEntriesNo}
WHERE team_name LIKE CONCAT('%',#{searchWord},'%')
ORDER BY team_no LIMIT #{startRow}, #{rowPerPage}

mapper.update:
UPDATE t_team 
SET team_name = #{teamName} 	
	,team_region = #{teamRegion}
	,team_ages = #{teamAges}
	,team_intro = #{teamIntro}
WHERE team_no = #{teamNo}

mapper.select:
SELECT t.team_no AS teamNo ,t.team_name AS teamName ,t.sport_entries_no AS sportEntriesNo FROM t_team t INNER JOIN t_team_member m ON t.team_no = m.team_no WHERE m.member_no = #{memberNo}
WHERE team_name LIKE CONCAT('%',#{searchWord},'%')
ORDER BY t.team_no LIMIT #{startRow}, #{rowPerPage}
